sys_variable_value.value:
(function execute(inputs, outputs) {
var docIntelAPI = new sn_docintel.DocIntelAPI();
var task_name = inputs.task_name ? inputs.task_name : "Task";
outputs.task_id = docIntelAPI.createTask(task_name, inputs.task_definition.sys_id, inputs.source_table_name, inputs.source_record_sysid);
docIntelAPI.processTask(outputs.task_id);
})(inputs, outputs);

